      public function isSeller() {
return  $this->seller_id;
}
public function getSellerGroupId() {
  return $this->seller_group_id;
}
public function getSellerProductStatus() {
  return $this->seller_product_status;
}
public function getSellerGroupProductStatus() {
$query = $this->db->query("SELECT product_status FROM " . DB_PREFIX . "seller_group WHERE seller_group_id = '" . (int)$this->seller_group_id . "'");

  return $query->row['product_status'];
}
public function getBankId() {
  return $this->bank_id;
}


